    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        Connection c = null;
        Statement stmt = null;
        //BOVLogin username = new BOVLogin();
        //String user = username.usrname;
    try {
      Class.forName("org.sqlite.JDBC");
      c = DriverManager.getConnection("jdbc:sqlite:bov.db");
      c.setAutoCommit(false);
      System.out.println("Opened database successfully");
      //search uname if uname found pop up window enter different uname else continue
      stmt = c.createStatement();
      ResultSet rs = stmt.executeQuery("SELECT id FROM inbox WHERE id ='"+jTextField1.getText()+"';");
      if(!rs.isBeforeFirst()){
          new idErr().setVisible(true);
      }
      else{
          String sql = "DELETE FROM inbox " +
                   "WHERE id = '"+jTextField1.getText()+"';" ; 
      
          stmt.executeUpdate(sql);
          new msgD().setVisible(true);
          this.dispose();
      }
      

      stmt.close();
      c.commit();
      c.close();
      
    }catch ( Exception e ) {
      System.err.println( e.getClass().getName() + ": " + e.getMessage() );
      System.exit(0);
    }// TODO add your handling code here:
    }//GEN-LAST:event_jButton2ActionPerformed

